<?php

namespace App\Imports;

use App\Models\Product;
use Maatwebsite\Excel\Concerns\ToModel;
use Illuminate\Contracts\Queue\ShouldQueue;
use Maatwebsite\Excel\Concerns\WithStartRow;

class ProductImport implements ToModel, WithStartRow
{
    /**
    * @param array $row
    *
    * @return \Illuminate\Database\Eloquent\Model|null
    */

    public function startRow(): int
    {
        return 2; // Skip the first row and start importing from the second row
    }

    public function model(array $row)
    {
        $product = Product::where('batch_code', $row['0'])->first();
        // dd($product);
        if ($product) {
            // If product with batch code already exists, update the quantity
            $product->available_qty += $row[2];
            $product->save();
        } else {
            $product = new Product([
            'batch_code' => $row[0],
            'product_name' => $row[1],
            'category_id' => '1',
            'available_qty' => $row[2],
        
        ]);
        $product->save();
    }
    return $product;

    }
}
